import { StyleSheet, Text, View } from "react-native";
import React from "react";

const UserProfile = () => {
  return (
    <>
      <View style={styles.header}>
        <Text style={{ fontSize: 30, marginLeft: 15 }}>
          {userInfo.userName}
        </Text>
        <TouchableOpacity>
          <Text style={{ marginRight: 15 }}>
            <AddPostIcon
              name="plussquareo"
              size={30}
              style={{ borderRadius: 50 }}
              onPress={() => navigation.navigate("upload")}
            />
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.mainContainer}>
        <View style={styles.main}>
          <View style={styles.Profile}>
            <Image
              source={{
                uri:
                  userInfo.profile == ""
                    ? "http://www.gravatar.com/avatar/?d=mp"
                    : userInfo.profile,
              }}
              style={styles.img}
            />
            <Text style={{ textAlign: "center", fontWeight: "600" }}></Text>
          </View>

          <View style={styles.posts}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>
              {data.length}
            </Text>
            <Text style={styles.bottomText}>Posts</Text>
          </View>

          <View style={styles.follwers}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followers</Text>
          </View>

          <View style={styles.following}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followings</Text>
          </View>
        </View>
        <TouchableOpacity style={styles.btn}>
          <Text style={{ textAlign: "center", fontWeight: "500" }}>
            Edit Profile
          </Text>
        </TouchableOpacity>
      </View>

      <Text style={{ textAlign: "center", fontSize: 30, marginBottom: 10 }}>
        Your Posts
      </Text>
      <FlatList
        style={{}}
        data={data}
        keyExtractor={(item) => item._id}
        renderItem={({ item }) => {
          // const { image, like } = item;
          return (
            <View>
              <Image
                source={{ uri: item.image }}
                style={{ height: 300, resizeMode: "cover" }}
              />

              <View style={styles.iconsContainer}>
                <View style={styles.likeContainer}>
                  <Like
                    name="like"
                    size={30}
                    backgroundColor="blue"
                    style={styles.like}
                  />
                  <Text style={{ fontWeight: "600" }}>
                    {" "}
                    {item.likes.length}like
                  </Text>
                </View>

                <View style={styles.commentContainer}>
                  <Comment name="comment" size={40} style={styles.comment} />
                  <Text style={{ textAlign: "center", fontWeight: "600" }}>
                    15 comment
                  </Text>
                </View>

                <View style={styles.shareContainer}>
                  <Share name="share" size={30} style={styles.share} />
                  <Text style={{ fontWeight: "600" }}>35 Share </Text>
                </View>
              </View>
            </View>
          );
        }}
      />
    </>
  );
};

export default UserProfile;

const styles = StyleSheet.create({});
