import React, { useState, useEffect, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  Modal,
  TextInput,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Profile from "../Profile";
import UserProfile from "../UserProfile";
import axios from "axios";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";
import Times from "react-native-vector-icons/FontAwesome";
import Send from "react-native-vector-icons/MaterialIcons";
import Dislike from "react-native-vector-icons/AntDesign";
import { useNavigation } from "@react-navigation/native";
import { AuthContext } from "./../Context/useContext";

const Main = () => {
  const [postData, setPostData] = useState([]);
  const [show, setShow] = useState(false);
  const [liked, setLiked] = useState(false);
  const [likesCount, setLikesCount] = useState([]);
  const [openModel, setOpenModel] = useState(false);
  const [comment, setComment] = useState("");
  const [postId, setPostId] = useState("");
  const [commentsData, setCommentsData] = useState([]);
  const navigation = useNavigation();
  const { userId } = useContext(AuthContext);

  const getData = async () => {
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    axios.get("http://192.168.0.106:5000/api/allpost").then((res) => {
      setPostData(res.data.data);
      console.log("res : ", res.data.data[0].postedBy.profile);
    });
  };

  useEffect(() => {
    getData();
  }, []);
  const likePost = async (postId) => {
    console.log("Liked post id ", postId);
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    axios
      .put(`http://192.168.0.106:5000/api/like/${postId}`, {
        userId: userData._id,
      })
      .then((res) => {
        console.log("Response getting from posts", res);
        console.log(res.data.result.likes);
        // console.log(res.data.result.likes);

        if (
          userData &&
          res.data.result.likes.includes(userData._id)
          // like[i] === userData._id
        ) {
          console.log("first");
          setLiked(true);
        } else {
          setLiked(false);
        }
        // const newData = postData.map((item) => {
        //   if (item._id == res._id) {
        //     return res;
        //   } else {
        //     return item;
        //   }
        // });
        // setPostData(newData);
      })
      .catch((err) => console.log(err));
  };

  const unLikePost = async (postId) => {
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    // console.log(userData);
    axios
      .put(`http://192.168.0.106:5000/api/unlike/${postId}`, {
        userId: userData._id,
      })
      .then((res) => {
        console.log("Unlike Response : ", res);
        setLikesCount(res.data.result.likes.length);
      })
      .catch((err) => console.log(err));
  };

  const commentPost = async (postId) => {
    axios
      .put(`http://192.168.0.106:5000/api/comment/${postId}`, {
        comment,
      })
      .then((res) => {
        console.log("Comment Response : ", res.data.result.comments);
        setCommentsData(res.data.result.comments);
      })
      .catch((err) => console.log(err));
  };
  return (
    <>
      <Text>{userId}</Text>
      <FlatList
        data={postData}
        renderItem={({ item, index }) => {
          // console.log("Items are :", item);
          // console.log("Likes :", index, item);
          return (
            <SafeAreaView style={styles.container}>
              <View style={styles.innerCont}>
                <Image
                  source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
                  style={styles.profile}
                />
                <Text
                  style={styles.name}
                  onPress={() =>
                    navigation.navigate(
                      id == item.postedBy._id
                        ? navigation.navigate("profile")
                        : navigation.navigate("userprofile", {
                            userId: item.postedBy._id,
                          })
                    )
                  }
                >
                  {item.postedBy.userName}
                </Text>
              </View>
              <View style={styles.childCont}>
                <Image source={{ uri: item.image }} style={styles.img} />
                <View style={styles.iconsContainer}>
                  <View style={styles.likeContainer}>
                    <TouchableOpacity>
                      {liked ? (
                        <Dislike
                          name="dislike2"
                          size={30}
                          style={styles.like}
                          onPress={() => unLikePost(item._id)}
                        />
                      ) : (
                        <Like
                          name="like"
                          size={30}
                          backgroundColor="blue"
                          style={styles.like}
                          onPress={() => likePost(item._id)}
                        />
                      )}
                    </TouchableOpacity>
                    <Text>
                      {show
                        ? `You and  ${item.likes.length} others like this post`
                        : ""}
                    </Text>

                    <Text>
                      {item.likes.length == 0 ? "" : item.likes.length}like
                    </Text>
                  </View>
                  <View style={styles.commentContainer}>
                    <TouchableOpacity>
                      <Comment
                        name="comment"
                        size={40}
                        style={styles.comment}
                        onPress={() => {
                          setPostId(item._id);
                          setOpenModel(true);
                        }}
                      />
                    </TouchableOpacity>
                    <Text>comment</Text>
                  </View>

                  <View style={styles.shareContainer}>
                    <TouchableOpacity>
                      <Share name="share" size={30} style={styles.share} />
                    </TouchableOpacity>

                    <Text>Share </Text>
                  </View>
                </View>
                <View style={styles.discription}>
                  <Text style={{ fontWeight: "bold" }}>
                    {item.content == "" ? "" : item.postedBy.userName + "  "}
                  </Text>
                  <Text>
                    {item.content.length > 25
                      ? item.content.slice(0, 25) + "..."
                      : item.content}
                  </Text>
                </View>
              </View>
            </SafeAreaView>
          );
        }}
      />

      <Modal visible={openModel} animationType="slide">
        <View>
          <TouchableOpacity>
            <Times
              name="times"
              size={30}
              style={{ margin: 20 }}
              onPress={() => setOpenModel(false)}
            />
          </TouchableOpacity>
          <View style={{ flexDirection: "row", position: "relative" }}>
            <TextInput
              style={styles.input}
              placeholder="Write Something here..."
              value={comment}
              onChangeText={(val) => setComment(val)}
            />
            <TouchableOpacity
              style={{ position: "absolute", right: 30, top: 5 }}
            >
              <Send name="send" size={25} onPress={() => commentPost(postId)} />
            </TouchableOpacity>
          </View>

          <FlatList
            data={commentsData}
            key={(item) => item._id}
            renderItem={({ item }) => {
              console.log("Items Are", item);
              return (
                <View style={styles.innerCont}>
                  <Image
                    source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
                    style={{ width: 25, height: 25, borderRadius: 50 }}
                  />
                  <Text style={{ fontSize: 15, marginLeft: 10 }}>
                    Zaid Siddiqui
                  </Text>
                  <Text style={{ marginLeft: 40 }}></Text>
                </View>
              );
            }}
          />

          <View style={styles.innerCont}>
            <Image
              source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
              style={{ width: 25, height: 25, borderRadius: 50 }}
            />
            <Text style={{ fontSize: 15, marginLeft: 10 }}>Zaid Siddiqui</Text>
          </View>
          <Text style={{ marginLeft: 40 }}>Comment</Text>
          <View style={styles.innerCont}>
            <Image
              source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
              style={{ width: 25, height: 25, borderRadius: 50 }}
            />
            <Text style={{ fontSize: 15, marginLeft: 10 }}>Zaid Siddiqui</Text>
          </View>
          <Text style={{ marginLeft: 40 }}>Comment</Text>
        </View>
      </Modal>
    </>
  );
};

export default Main;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#fff",
  },
  innerCont: {
    marginTop: 20,
    marginLeft: 20,
    flexDirection: "row",
  },
  name: {
    fontSize: 20,
    marginLeft: 10,
  },
  childCont: {
    marginTop: 10,
  },
  img: {
    width: "100%",
    height: 400,
    // aspectRatio: 1,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    justifyContent: "space-around",
  },
  discription: {
    flexDirection: "row",
    marginTop: 10,
    marginLeft: 20,
  },
  profile: {
    // marginLeft: 10,
    width: 30,
    height: 30,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },

  input: {
    width: "90%",
    borderWidth: 1,
    borderColor: "black",
    color: "#000",
    borderRadius: 7,
    marginHorizontal: 20,
    paddingVertical: 6,
    paddingHorizontal: 10,
    // marginBottom: 20,
    // color: "#eee",
    fontSize: 15,
  },
});
