import {
  FlatList,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import Send from "react-native-vector-icons/FontAwesome";
import React, { useState, useContext, useEffect } from "react";
import { AuthContext } from "./Context/useContext";
import { getMessages } from "./Conversation";
import { Route } from "@react-navigation/native";
import axios from "axios";

const Message = ({ route }) => {
  const { userId } = useContext(AuthContext);
  const [user, setUser] = useState([]);
  const [messages, setMessages] = useState([]);
  const { conversationId, friendId } = route.params;
  console.log(conversationId, " +  " + friendId);

  const getUser = async () => {
    try {
      const res = await axios(
        `http://192.168.0.106:5000/api/profile/${friendId}`
      );
      console.log(res);
      // setUser(res.data.user);
    } catch (err) {
      console.log(err);
    }
  };
  const getMessages = async (conversationId) => {
    console.log("conversationId", conversationId);
    try {
      const res = await axios.get(
        `http://192.168.0.106:5000/chat/getmessage/${conversationId}`
      );
      console.log(res);
      // setMessages(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getUser();
    getMessages();
  }, []);

  return (
    <>
      <View style={{ backgroundColor: "#f1c40f", paddingVertical: 20 }}>
        <Text
          style={{
            alignSelf: "center",
            marginTop: 30,
            fontSize: 20,
            backgroundColor: "#f1c40f",
          }}
        >
          Zaid Siddiqi
        </Text>
      </View>

      {/* <FlatList
        data={data}
        keyExtractor={(key) => key.id}
        renderItem={({ item }) => {
          return (
            <SafeAreaView>
              <View style={userId ? styles.CurrentUser : styles.User}>
                <View style={styles.message}>
                  <Text>{item.text}</Text>
                </View>
                <Text style={{ marginRight: 20 }}> 8:30 Am</Text>
              </View>
            </SafeAreaView>
          );
        }}
      /> */}

      <Textinput />
    </>
  );
};

export const Textinput = () => {
  return (
    <View
      style={{
        justifyContent: "center",
        alignItems: "center",
        marginVertical: 20,
        position: "relative",
      }}
    >
      <TextInput
        placeholder="Enter a Mesage"
        style={{
          borderWidth: 1,
          borderColor: "#000",
          padding: 7,
          width: 350,
          borderRadius: 15,
        }}
      />
      <View style={{ position: "absolute", marginLeft: 50 }}>
        <TouchableOpacity>
          <Text>
            <Send name="send" size={25} />
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  message: {
    borderRadius: 20,
    padding: 10,
    borderWidth: 1,
    borderColor: "#000",
    // margin: 10,
    width: 200,
  },
  CurrentUser: {
    justifyContent: "flex-end",
    alignItems: "flex-end",
    margin: 10,
  },
  User: {
    justifyContent: "flex-start",
    alignItems: "flex-start",
    margin: 10,
  },
});
export default Message;
