import {
  Button,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React from "react";
import * as ImagePicker from "expo-image-picker";

const Upload = () => {
  const pickImage = async () => {
    let data = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.5,
    });
    if (!data.cancelled) {
      handleUpload(data.uri);
    }
  };
  return (
    <View style={styles.wrapper}>
      <Text style={{ fontSize: 30, textAlign: "center" }}>Upload</Text>

      <View style={styles.container}>
        <TouchableOpacity>
          <View style={styles.imgBox} onPress={() => pickImage()}></View>
        </TouchableOpacity>

        <View style={{}}>
          <TextInput
            style={styles.inputStyle}
            placeholder="Write Something here..."
          />
        </View>
      </View>

      <View style={styles.buttonStyle}>
        <Button title="POST" style={{}} />
      </View>
    </View>
  );
};

export default Upload;

const styles = StyleSheet.create({
  wrapper: {
    marginTop: 50,
  },
  container: {
    marginTop: 20,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginBottom: 30,
  },
  imgBox: {
    borderWidth: 1,
    borderColor: "Black",
    width: 120,
    height: 120,
    marginRight: 10,
  },
  inputStyle: {
    borderWidth: 1,
    borderColor: "Black",
    height: 120,
    width: 200,
    fontWeight: "600",
    fontSize: 18,
    padding: 0,
  },
  buttonStyle: {
    marginHorizontal: 20,
  },
});
