import { FlatList, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React from "react";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";
import AddPostIcon from "react-native-vector-icons/AntDesign";
import ViewPostIcon from "react-native-vector-icons/MaterialCommunityIcons";
import Images from "./Data/ImageData";
import { Image } from "react-native";

export default function Profile() {
  return (
    <View>
      <View style={styles.header}>
        <Text style={{ fontSize: 30, marginLeft: 15 }}>Username</Text>
        <TouchableOpacity>
          <Text style={{ marginRight: 15 }}>
            <AddPostIcon name="plussquareo" size={30} style={{borderRadius:50}} />
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.mainContainer}>
        <View style={styles.main}>
          <View style={styles.Profile}>
            <Image source={require("../assets/img1.jpeg")} style={styles.img} />
            <Text style={{ textAlign: "center", fontWeight: "600" }}>
              Zaid Siddqui
            </Text>
          </View>

          <View style={styles.posts}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>Posts</Text>
          </View>

          <View style={styles.follwers}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followers</Text>
          </View>

          <View style={styles.following}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followings</Text>
          </View>
         
        </View>
        <TouchableOpacity style={styles.btn}>
          <Text style={{textAlign:"center",fontWeight:'500',}}>Edit Profile</Text>
        </TouchableOpacity>

      </View>

      <View style={styles.postContainer} >
      <Image source={require("../assets/img1.jpeg")} style={{height:300,resizeMode:"cover",}} />
      <View style={styles.iconsContainer}>
                  <View style={styles.likeContainer}>
                    <Like name="like" size={30} backgroundColor="blue" style={styles.like} />
                    <Text>{item.like} like</Text>
                  </View>
                  <View style={styles.commentContainer}>
                    <Comment name="comment" size={40} style={styles.comment} />
                    <Text>comment</Text>
                  </View>
    
                  <View style={styles.shareContainer}>
                    <Share name="share" size={30} style={styles.share} />
                    <Text>Share </Text>
                  </View>
                </View>
         {/* <FlatList 
          data={Images}
          keyExtractor={(item)=>item.id}
          renderItem={({item}) => {
            return (
            <View>
               <TouchableOpacity>
                <Text style={styles.image}><Image source={item.img} /></Text>
               </TouchableOpacity>
            </View>
  )
          }}
        /> */}

      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    marginTop: 40,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  main: {
    flexDirection: "row",
    justifyContent: "space-around",
    position:"relative",
    marginTop: 30,
  },
  img: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
  bottomText: {
    fontWeight: "400",
  },
  btn:{
    borderWidth:1,
    borderColor:"#000",
    width:"60%",
    borderRadius:5,
    paddingVertical:3,
    textAlign:"center",
    position:"absolute",
    top:80,
    left:130,
    alignSelf:"center",
  },
  postContainer:{
    marginTop:30,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    justifyContent: "space-around",
  },
});
