import { StyleSheet, Text, View,TouchableOpacity,Image } from 'react-native'
import React from 'react'

export default function Notifications() {
  return (
    <View>
        <View style={{marginTop:45,marginLeft:20}}>
      <Text style={{fontSize:23,fontWeight:"500"}}>Notifications</Text>

      <TouchableOpacity>
          <View style={styles.chatSection}>
            <View style={{ flexDirection: "row", position: "relative" }}>
              <TouchableOpacity>
                <Image
                  source={require("../assets/img2.jpeg")}
                  style={styles.img}
                />
              </TouchableOpacity>
              <Text style={{ marginLeft: 10, fontWeight: "500", fontSize: 20}}>
                Zaid Siddiqui
              </Text>
            </View>

            <View style={{ position: "absolute", top: 25, left: 60 }}>
              <Text>Last Chat Message 2d</Text>
            </View>
          </View>
        </TouchableOpacity  >
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
    chatSection: {
        marginTop: 20,
        // flexDirection:'row',
      },
      img: {
        width: 50,
        height: 50,
        borderRadius: 50,
      },
})
