import { StyleSheet, Text, View,Image, ScrollView } from "react-native";
import React from "react";
import Chat from "react-native-vector-icons/Ionicons";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";

export default function Home() {
  return (
    <View>
    <View style={styles.navbar}>
      <View style={styles.nav}>
        <Text style={styles.logo}>chattify</Text>
        <Text style={styles.chatIcon}><Chat name="md-chatbubble-ellipses-outline" size={30} /></Text>
      </View>
    </View>


    <View style={styles.container}>
    <ScrollView>
      <View style={styles.innerCont}>
        <Image source={require("../assets/img1.jpeg")} style={styles.profile} />
        <Text style={styles.name}>Zaid siddiqui</Text>
      </View>
      <View style={styles.childCont}>
        <Image source={require("../assets/img1.jpeg")} style={styles.img} />
        <Like name="like" size={20}/>
        <Comment name="comment" size={20}/>
        <Share name="share" size={20}/>

      </View>
      </ScrollView>
    </View>
    {/* </ScrollView> */}
    </View>
 
  );
}

const styles = StyleSheet.create({
  navbar: {
    marginTop: 50,
    width: "100%",
    height: 60,
    backgroundColor:"grey"
   
    
  },
  nav:{
    display: "flex",
   justifyContent:"space-between",
   flexDirection:"row",
   alignItems:"center",
  },
  logo:{
   fontSize:25,
    marginLeft:20,
    marginTop:12,
  },
  chatIcon:{
    marginRight:20,
    marginTop:12,
  },
  profile:{
    width:30,
    height:30,
   borderRadius:50,
  },
  innerCont:{
    marginTop:20,
    marginLeft:20,
    flexDirection:"row",
  },
  name:{
    fontSize:20,
    marginLeft:10,
  },
  childCont:{
    marginTop:10,
    width:"100%",
    height:500,
  }

  

});
