import { StyleSheet, Text, View, Image } from "react-native";
import React, { useState, useEffect } from "react";
import Axios from "axios";

const News = () => {
  const [data, setData] = useState([]);
  const getData = () => {
    Axios.get(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=55d7bf49be2b471e823fc66cacc0fb93"
    )
      .then((res) => {
        console.log(res);
        setData(res.data.articles);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 25 }}>News</Text>
      <View style={styles.card}>
        <Image source={require("../assets/img1.jpeg")} style={styles.image} />
        <Text style={{ marginTop: 5, fontWeight: "600", marginLeft: 10 }}>
          Queen Elizabeth II lying in state, Prince William, Prince Harry hold
          vigil - Fox News
        </Text>
        <Text style={{ marginTop: 5, fontWeight: "400", marginLeft: 10 }}>
          The Queen lies-in-state for a third day in Westminster Hall Saturday.
          Her grandchildren the Prince of Wales, William, and Prince Harry stood
          vigil over her coffin, after King Charles III and his siblings stood
          vigil Friday.
        </Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            marginTop: 10,
            marginHorizontal: 10,
          }}
        >
          <Text style={{ fontWeight: "350" }}>
            By : <Text style={{ fontWeight: "600" }}>Zaid Sidiqi</Text>
          </Text>
          <Text style={{ fontWeight: "600" }}>Date </Text>
        </View>
        <Text style={{ marginTop: 10, fontWeight: "600" }}>Source :</Text>
      </View>
    </View>
  );
};

export default News;

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    alignSelf: "center",
    width: "90%",
  },
  card: {
    marginTop: 40,
    borderWidth: 1,
    borderColor: "#000",
    alignSelf: "center",
    width: 300,
    // borderRadius: 30,
  },
  image: {
    width: "100%",
    height: 200,
    // aspectRatio: 1,
  },
});
