import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";
import React, { useState, useContext, useEffect } from "react";
import Chat from "react-native-vector-icons/Ionicons";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import HomeIcon from "react-native-vector-icons/AntDesign";
import Search from "react-native-vector-icons/AntDesign";
import Notify from "react-native-vector-icons/Ionicons";
import Share from "react-native-vector-icons/FontAwesome";
import ShadedHomeIcon from "react-native-vector-icons/Entypo";
import ShadedSearchIcon from "react-native-vector-icons/FontAwesome";
import ShadedNotifyIcon from "react-native-vector-icons/Ionicons";
import Logout from "react-native-vector-icons/AntDesign";
import AddPostIcon from "react-native-vector-icons/Ionicons";
import { AuthContext } from "./Context/useContext";
import { useNavigation } from "@react-navigation/native";
import data from "./Data/Data";
import Login from "./Login";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Upload from "./Upload";
import axios from "axios";

export const Apps = () => {
  const navigation = useNavigation();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState("");

  const retrieveData = async () => {
    const savedData = await AsyncStorage.getItem("token");
    const userData = JSON.parse(await AsyncStorage.getItem("user"));
    console.log("ID: ", userData._id);
    setUserId(userData._id);
    console.log("Saved Data is", savedData);
    console.log("user Data is", userData);
    setToken(savedData);
  };

  retrieveData();

  return (
    <>
      {token ? <Home /> : <Login />}
      <Upload userId="zaid" />
    </>
  );
};

export default function Home() {
  return (
    <>
      <Navbar />
      <Navigations />
      <Main />
    </>
  );
}

export const Navbar = () => {
  const navigation = useNavigation();
  const loggedOut = async () => {
    const clearLs = AsyncStorage.clear();
    if (clearLs) {
      navigation.navigate("login");
    } else {
      alert("Storage is not cleared");
      navigation.navigate("home");
    }
  };
  return (
    <View style={styles.navbar}>
      <View style={styles.nav}>
        <Text style={styles.logo}>chattify</Text>
        <Text style={styles.chatIcon}>
          <TouchableOpacity onPress={() => navigation.navigate("upload")}>
            <AddPostIcon
              name="add-circle"
              size={30}
              style={{ paddingHorizontal: 10 }}
            />
          </TouchableOpacity>

          <TouchableOpacity onPress={() => navigation.navigate("chat")}>
            <Chat name="md-chatbubble-ellipses-outline" size={30} />
          </TouchableOpacity>

          <TouchableOpacity>
            <Logout name="logout" size={30} onPress={() => loggedOut()} />
          </TouchableOpacity>
        </Text>
      </View>
    </View>
  );
};

export const Navigations = () => {
  const navigation = useNavigation();

  const [icon, setIcon] = useState("home");
  return (
    <SafeAreaView style={styles.footer}>
      <TouchableOpacity
        onPress={() => {
          setIcon("home");
          navigation.navigate("home");
        }}
      >
        {icon === "home" ? (
          <Text>
            <ShadedHomeIcon name="home" size={25} />
          </Text>
        ) : (
          <Text>
            <HomeIcon name="home" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          // clickFuction
          setIcon("search");
          navigation.navigate("search");
        }}
      >
        {icon === "search" ? (
          <Text>
            <ShadedSearchIcon name="search" size={25} />
          </Text>
        ) : (
          <Text>
            <Search name="search1" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("notification");
          setIcon("notify");
        }}
      >
        {icon === "notify" ? (
          <Text>
            <ShadedNotifyIcon name="notifications" size={25} />
          </Text>
        ) : (
          <Text>
            <Notify name="notifications-outline" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          setIcon("profile");
          navigation.navigate("profile");
        }}
      >
        <Image source={require("../assets/img1.jpeg")} style={styles.profile} />
      </TouchableOpacity>
    </SafeAreaView>
  );
};

export const Main = () => {
  const [data, setData] = useState([]);
  const navigation = useNavigation();

  const getData = () => {
    axios
      .get("http://localhost:5000/api/allpost")
      .then((res) => {
        console.log("res : ", res.data);
        setData(res.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getData();
  });

  //   return (
  //     <View style={styles.wrapper}>
  //       <FlatList
  //         data={data}
  //         keyExtractor={(item) => item.id}
  //         renderItem={({ item }) => {
