  //           return (
  //             <SafeAreaView style={styles.container}>
  //               <View style={styles.innerCont}>
  //                 <Image source={item.profile} style={styles.profile} />
  //                 <Text style={styles.name}>{item.name}</Text>
  //               </View>
  //               <View style={styles.childCont}>
  //                 <Image source={item.image} style={styles.img} />
  //                 <View style={styles.iconsContainer}>
  //                   <View style={styles.likeContainer}>
  //                     <TouchableOpacity>
  //                       <Like
  //                         name="like"
  //                         size={30}
  //                         backgroundColor="blue"
  //                         style={styles.like}
  //                       />
  //                     </TouchableOpacity>

  //                     <Text>{item.like} like</Text>
  //                   </View>
  //                   <View style={styles.commentContainer}>
  //                     <TouchableOpacity>
  //                       <Comment
  //                         name="comment"
  //                         size={40}
  //                         style={styles.comment}
  //                       />
  //                     </TouchableOpacity>
  //                     <Text>comment</Text>
  //                   </View>

  //                   <View style={styles.shareContainer}>
  //                     <TouchableOpacity>
  //                       <Share name="share" size={30} style={styles.share} />
  //                     </TouchableOpacity>

  //                     <Text>Share </Text>
  //                   </View>
  //                 </View>
  //                 <View style={styles.discription}>
  //                   <Text style={{ fontWeight: "bold" }}> {item.name}</Text>
  //                   <Text>
  //                     {item.content.length > 25
  //                       ? item.content.slice(0, 25) + "..."
  //                       : item.content}
  //                   </Text>
  //                 </View>
  //               </View>
  //             </SafeAreaView>
  //           );
  //         }}
  //       />
  //     </View>
  //   );
};

const styles = StyleSheet.create({
  navbar: {
    marginTop: 50,
    width: "100%",
    height: 60,
    backgroundColor: "grey",
  },
  nav: {
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "row",
    alignItems: "center",
  },
  logo: {
    fontSize: 25,
    marginLeft: 20,
    marginTop: 12,
  },
  chatIcon: {
    marginRight: 20,
    marginTop: 12,
  },
  profile: {
    width: 30,
    height: 30,
    borderRadius: 50,
  },
  innerCont: {
    marginTop: 20,
    marginLeft: 20,
    flexDirection: "row",
  },
  name: {
    fontSize: 20,
    marginLeft: 10,
  },
  childCont: {
    marginTop: 10,
  },
  img: {
    width: "100%",
    height: 400,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    justifyContent: "space-around",
  },
  discription: {
    flexDirection: "row",
    marginTop: 10,
    marginLeft: 20,
  },
  footer: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingVertical: 10,
  },
});
