import { StyleSheet, Text, View,Button ,TextInput, TouchableOpacity, Linking} from "react-native";
import React from "react";
import App from './../App';
import Icons from "react-native-vector-icons/FontAwesome"
import Eye from "react-native-vector-icons/FontAwesome5"

export default function Login() {
  return (
    <View style={styles.Wrapper}>
    <View style={styles.container}>
      <Text style={styles.logo}>App Name</Text>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Enter Username or Mobile Number</Text>

        <View style={styles.inputGroup}>
        <TextInput style={styles.input} placeholder="Username" />
        <Eye name="eye-slash" size={20}/>
        </View>
        

        <Text style={styles.label}>Enter Password</Text>
        <TextInput
          style={styles.input}
          secureTextEntry={true}
          placeholder="Password"
        />

        <Button title="Submit " />
      </View>

      <View style={styles.bottomView}>
        <Text style={styles.bottomText}>Forgot your login details? Get help signing in.</Text>
        <Text style={styles.Or}>OR</Text>

        <TouchableOpacity style={styles.btn}>
            <Text style={styles.text}>Login with Facebook   <Icons name="facebook-square" size={20}/> </Text>
          
            
        </TouchableOpacity>
      </View>

      <View style={styles.lastContainer}>
        <Text style={styles.signup}>
             Don't have an account? 
             <Text  onPress={() => Linking.openURL("https://www.facebook.com/profile.php?id=100010376895535")}>sign up</Text>
        </Text>
       
      </View>
    </View>
    </View>
  );
}

const styles = StyleSheet.create({
    Wrapper:{
        width:'100%',
        height:'100%',
        backgroundColor:'#C13584',
    },
    container:{
        marginTop:200,
    },
    logo:{
      textAlign:"center",
      fontSize:35,
      marginBottom:30,
    },
    inputContainer:{
      marginHorizontal:30
    },
    input:{
        borderWidth:1,
        borderColor:'black',
        borderRadius:7,
        paddingVertical:6,
        paddingHorizontal:10,
        marginBottom:20,  
        color:"#eee",
        fontSize:15,
    },
    label:{
        marginBottom:5,
    },
    bottomView:{
      justifyContent:"center",
      alignItems:"center",
      marginTop:20,
    },
    Or:{
        marginTop:10,
    },
    btn:{
        marginTop:20,
        borderWidth: 1,
        borderColor: "#000",
        paddingHorizontal:80,
        paddingVertical:5,
        textAlign:"center",
        borderRadius:5,

    },
    lastContainer:{
        display:"flex",
      marginTop:180,
      alignItems:"center",
      borderTopWidth:1,
     marginHorizontal:30,
      borderColor: "#000",
    },
});
