import {
  FlatList,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
} from "react-native";
import React, { useState } from "react";

const Message = () => {
  const [data, setData] = useState([
    {
      id: 1,
      text: "hello",
    },
    {
      id: 2,
      text: "Heyy",
    },
    {
      id: 3,
      text: "WhatsaUpp",
    },
    {
      id: 4,
      text: "Alhamdulillah",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
    {
      id: 5,
      text: "What about You ? ",
    },
  ]);
  return (
    <View>
      <View style={{ backgroundColor: "#f1c40f", paddingVertical: 20 }}>
        <Text
          style={{
            alignSelf: "center",
            marginTop: 30,
            fontSize: 20,
            backgroundColor: "#f1c40f",
          }}
        >
          Zaid Siddiqi
        </Text>
      </View>

      <>
        <ScrollView>
          <View>
            <FlatList
              data={data}
              keyExtractor={(key) => key.id}
              renderItem={({ item }) => {
                return (
                  <SafeAreaView>
                    <View
                      style={{
                        justifyContent: "flex-end",
                        alignItems: "flex-end",
                        margin: 10,
                      }}
                    >
                      <View style={styles.message}>
                        <Text>{item.text}</Text>
                      </View>
                      <Text style={{ marginRight: 20 }}> 8:30 Am</Text>
                    </View>
                  </SafeAreaView>
                );
              }}
            />
          </View>
        </ScrollView>
      </>
    </View>
  );
};

export const Textinput = () => {
  return (
    <>
      <Text>
        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
            marginTop: 280,
          }}
        >
          <TextInput
            placeholder="Enter a Mesage"
            style={{
              borderWidth: 1,
              borderColor: "#000",
              padding: 7,
              width: 300,
              borderRadius: 15,
            }}
          />
        </View>
      </Text>
    </>
  );
};

const styles = StyleSheet.create({
  message: {
    borderRadius: 20,
    padding: 10,
    borderWidth: 1,
    borderColor: "#000",
    // margin: 10,
    width: 200,
  },
});
export default Message;
