import {
  StyleSheet,
  Text,
  View,
  Button,
  TextInput,
  TouchableOpacity,
  Linking,
} from "react-native";
import React, { useState } from "react";
import Icons from "react-native-vector-icons/FontAwesome";
import Eye from "react-native-vector-icons/FontAwesome5";
import Email from "react-native-vector-icons/MaterialCommunityIcons";
import { useNavigation } from "@react-navigation/native";
import * as ImagePicker from "expo-image-picker";

export default function SignUp() {
  const [image, setImage] = useState(null);
  const [show, setShow] = useState(false);

  const navigation = useNavigation();

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  return (
    <View>
      <View style={styles.Wrapper}>
        <View style={styles.container}>
          <Text style={styles.logo}>App Name</Text>
          <View style={styles.inputContainer}>
            <Text style={styles.label}>Enter Username </Text>

            <View style={styles.inputGroup}>
              <Text style={styles.icon}>
                <Eye name="user" size={15} color={"black"} />
              </Text>
              <TextInput style={styles.input} placeholder="Username" />
            </View>

            <Text style={styles.label}>Enter Name </Text>
            <View style={styles.inputGroup}>
              <Text style={styles.icon}>
                <Eye name="user" size={15} color={"black"} />
              </Text>
              <TextInput style={styles.input} placeholder="Full Name" />
            </View>

            <Text>Enter Email</Text>
            <View style={styles.inputGroup}>
              <Text style={styles.icon}>
                <Email name="email" size={15} color={"black"} />
              </Text>
              <TextInput
                style={styles.input}
                placeholder="xyz@gmail.com"
                keyboardType="email-address"
              />
            </View>

            <Text style={styles.label}>Enter Password</Text>
            <View style={styles.inputGroup}>
              <TouchableOpacity onPress={() => setShow(true)}>
                <Eye
                  name={show ? "eye" : "eye-slash"}
                  style={styles.icon}
                  size={15}
                  color={"black"}
                />
              </TouchableOpacity>

              <TextInput
                style={styles.input}
                secureTextEntry={show ? false : true}
                placeholder="Password"
              />
            </View>

            <Text style={styles.label}>Confirm Password</Text>
            <View style={styles.inputGroup}>
              <TouchableOpacity onPress={() => setShow(true)}>
                <Eye
                  name={show ? "eye" : "eye-slash"}
                  style={styles.icon}
                  size={15}
                  color={"black"}
                />
              </TouchableOpacity>

              <TextInput
                style={styles.input}
                secureTextEntry={show ? false : true}
                placeholder="Password"
              />
            </View>

            <View
              style={{
                borderWidth: 1,
                borderColor: "#000",
                paddingVertical: 5,
                paddingHorizontal: 10,
                borderRadius: 10,
                marginBottom: 10,
              }}
            >
              <TouchableOpacity onPress={pickImage}>
                <Text>Upload Profile Pic</Text>
              </TouchableOpacity>
              {image && (
                <Image
                  source={{ uri: image }}
                  style={{ width: 200, height: 200 }}
                />
              )}
            </View>

            <Button title="Submit " style={{ marginTop: 10 }} />
          </View>

          <View style={styles.lastContainer}>
            <Text style={styles.signup}>
              Already have an account?
              <Text
                style={{
                  fontWeight: "bold",
                  borderBottomWidth: 1,
                  borderColor: "#000",
                }}
                onPress={() => navigation.navigate("login")}
              >
                Sign in
              </Text>
            </Text>
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  Wrapper: {
    width: "100%",
    height: "100%",
    backgroundColor: "#C13584",
  },
  container: {
    marginTop: 200,
  },
  logo: {
    textAlign: "center",
    fontSize: 35,
    marginBottom: 30,
  },
  inputContainer: {
    marginHorizontal: 30,
  },
  input: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 7,
    paddingVertical: 6,
    paddingHorizontal: 10,
    marginBottom: 20,
    color: "#eee",
    fontSize: 15,
  },
  label: {
    marginBottom: 5,
  },
  bottomView: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 20,
  },
  Or: {
    marginTop: 10,
  },
  btn: {
    marginTop: 20,
    borderWidth: 1,
    borderColor: "#000",
    paddingHorizontal: 80,
    paddingVertical: 5,
    textAlign: "center",
    borderRadius: 5,
  },
  lastContainer: {
    display: "flex",
    marginTop: 20,
    alignItems: "center",
    borderTopWidth: 1,
    marginHorizontal: 30,
    borderColor: "#000",
  },
  inputGroup: {
    position: "relative",
  },
  icon: {
    position: "absolute",
    right: 15,
    top: 15,
  },
});
