import {
  FlatList,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  Image,
} from "react-native";
import React, { useContext, useEffect, useState } from "react";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";
import AddPostIcon from "react-native-vector-icons/AntDesign";
import ViewPostIcon from "react-native-vector-icons/MaterialCommunityIcons";
import { AuthContext } from "./Context/useContext";
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

export default function Profile() {
  // (async () => getResponse())();
  const { userData } = useContext(AuthContext);
  const [userInfo, setUserInfo] = useState([]);
  const navigation = useNavigation();

  useEffect(() => {
    getData();
  }, []);

  const [data, setData] = useState([]);

  // useEffect(() => {
  //   (async () => getData())();
  // }, []);

  const getData = async () => {
    const id = JSON.parse(await AsyncStorage.getItem("userid"));
    console.log("Saved Data is :", id);

    // Fetching user data
    axios
      .get(`http://192.168.0.106:5000/api/profile/${id}`)
      .then((res) => {
        console.log("User Data is : ", res.data.user);
        setUserInfo(res.data.user);
      })
      .catch((err) => {
        console.log(err);
      });

    // fetching post Data
    axios
      .get(`http://192.168.0.106:5000/api/mypost/${id}`)
      .then((res) => {
        setData(res.data.data);
        console.log(res.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <View style={styles.header}>
        <Text style={{ fontSize: 30, marginLeft: 15 }}>
          {userInfo.userName}
        </Text>
        <TouchableOpacity>
          <Text style={{ marginRight: 15 }}>
            <AddPostIcon
              name="plussquareo"
              size={30}
              style={{ borderRadius: 50 }}
              onPress={() => navigation.navigate("upload")}
            />
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.mainContainer}>
        <View style={styles.main}>
          <View style={styles.Profile}>
            <Image
              source={{
                uri:
                  userInfo.profile == ""
                    ? "http://www.gravatar.com/avatar/?d=mp"
                    : userInfo.profile,
              }}
              style={styles.img}
            />
            <Text style={{ textAlign: "center", fontWeight: "600" }}></Text>
          </View>

          <View style={styles.posts}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>
              {data.length}
            </Text>
            <Text style={styles.bottomText}>Posts</Text>
          </View>

          <View style={styles.follwers}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followers</Text>
          </View>

          <View style={styles.following}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followings</Text>
          </View>
        </View>
        <TouchableOpacity style={styles.btn}>
          <Text style={{ textAlign: "center", fontWeight: "500" }}>
            Edit Profile
          </Text>
        </TouchableOpacity>
      </View>

      <Text style={{ textAlign: "center", fontSize: 30, marginBottom: 10 }}>
        Your Posts
      </Text>
      <FlatList
        data={data}
        keyExtractor={(item) => item._id}
        renderItem={({ item }) => {
          // const { image, like } = item;
          return (
            <View>
              <Image
                source={{ uri: item.image }}
                style={{ height: 300, resizeMode: "cover" }}
              />

              <View style={styles.iconsContainer}>
                <View style={styles.likeContainer}>
                  <Like
                    name="like"
                    size={30}
                    backgroundColor="blue"
                    style={styles.like}
                  />
                  <Text style={{ fontWeight: "600" }}>
                    {item.likes.length}like
                  </Text>
                </View>

                <View style={styles.commentContainer}>
                  <Comment name="comment" size={40} style={styles.comment} />
                  <Text style={{ textAlign: "center", fontWeight: "600" }}>
                    15 comment
                  </Text>
                </View>

                <View style={styles.shareContainer}>
                  <Share name="share" size={30} style={styles.share} />
                  <Text style={{ fontWeight: "600" }}>35 Share </Text>
                </View>
              </View>
            </View>
          );
        }}
      />
    </>
  );
}

const styles = StyleSheet.create({
  header: {
    marginTop: 40,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  main: {
    flexDirection: "row",
    justifyContent: "space-around",
    position: "relative",
    marginTop: 30,
  },
  img: {
    width: 100,
    height: 100,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },
  bottomText: {
    fontWeight: "400",
  },
  btn: {
    borderWidth: 1,
    borderColor: "#000",
    width: "60%",
    borderRadius: 5,
    paddingVertical: 3,
    textAlign: "center",
    position: "absolute",
    top: 80,
    left: 130,
    alignSelf: "center",
  },
  postContainer: {
    marginTop: 20,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    marginBottom: 20,
    justifyContent: "space-around",
    alignItems: "center",
  },
});
