import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";
import React, { useState, useContext } from "react";
import Chat from "react-native-vector-icons/Ionicons";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import HomeIcon from "react-native-vector-icons/AntDesign";
import Search from "react-native-vector-icons/AntDesign";
import Notify from "react-native-vector-icons/Ionicons";
import Share from "react-native-vector-icons/FontAwesome";
import ShadedHomeIcon from "react-native-vector-icons/Entypo";
import ShadedSearchIcon from "react-native-vector-icons/FontAwesome";
import ShadedNotifyIcon from "react-native-vector-icons/Ionicons";
import Logout from "react-native-vector-icons/AntDesign";
import AddPostIcon from "react-native-vector-icons/Ionicons";
import { AuthContext } from "./Context/useContext";
import { useNavigation } from "@react-navigation/native";
import data from "./Data/Data";
import Login from "./Login";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const Apps = () => {
  const { isLoading } = useContext(AuthContext);
  if (isLoading) {
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <ActivityIndicator size="large" />
    </View>;
  }
  let StringToken = JSON.stringify("token");
  let tokenData = AsyncStorage.getItem(StringToken);
  return <>{tokenData !== null ? <Home /> : <Login />}</>;
};

export default function Home() {
  return (
    <>
      <Navbar />
      <Navigations />
      <Main />
    </>
  );
}

export const Navbar = () => {
  const navigation = useNavigation();
  const loggedOut = async () => {
    try {
      let clearData = AsyncStorage.removeItem("token");
      if (clearData) {
        alert("Successfully deleted");
        let getData = AsyncStorage.getItem("token");
        console.log("Token is ", JSON.stringify(getData));
      } else {
        alert("Error Occured");
      }
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <View style={styles.navbar}>
      <View style={styles.nav}>
        <Text style={styles.logo}>chattify</Text>
        <Text style={styles.chatIcon}>
          <TouchableOpacity>
            <AddPostIcon
              name="add-circle"
              size={30}
              style={{ paddingHorizontal: 10 }}
            />
          </TouchableOpacity>

          <TouchableOpacity onPress={() => navigation.navigate("chat")}>
            <Chat name="md-chatbubble-ellipses-outline" size={30} />
          </TouchableOpacity>

          <TouchableOpacity>
            <Logout name="logout" size={30} onPress={() => loggedOut()} />
          </TouchableOpacity>
        </Text>
      </View>
    </View>
  );
};

export const Navigations = () => {
  const navigation = useNavigation();

  const [icon, setIcon] = useState("home");
  return (
    <SafeAreaView style={styles.footer}>
      <TouchableOpacity
        onPress={() => {
          setIcon("home");
          navigation.navigate("home");
        }}
      >
        {icon === "home" ? (
          <Text>
            <ShadedHomeIcon name="home" size={25} />
          </Text>
        ) : (
          <Text>
            <HomeIcon name="home" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          // clickFuction
          setIcon("search");
          navigation.navigate("search");
        }}
      >
        {icon === "search" ? (
          <Text>
            <ShadedSearchIcon name="search" size={25} />
          </Text>
        ) : (
          <Text>
            <Search name="search1" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("notification");
          setIcon("notify");
        }}
      >
        {icon === "notify" ? (
          <Text>
            <ShadedNotifyIcon name="notifications" size={25} />
          </Text>
        ) : (
          <Text>
            <Notify name="notifications-outline" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          setIcon("profile");
          navigation.navigate("profile");
        }}
      >
        <Image source={require("../assets/img1.jpeg")} style={styles.profile} />
      </TouchableOpacity>
    </SafeAreaView>
  );
};

export const Main = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.wrapper}>
      <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => {
          console.log(item);
          return (
            <SafeAreaView style={styles.container}>
              <View style={styles.innerCont}>
                <Image source={item.profile} style={styles.profile} />
                <Text style={styles.name}>{item.name}</Text>
              </View>
              <View style={styles.childCont}>
                <Image source={item.image} style={styles.img} />
                <View style={styles.iconsContainer}>
                  <View style={styles.likeContainer}>
                    <TouchableOpacity>
                      <Like
                        name="like"
                        size={30}
                        backgroundColor="blue"
                        style={styles.like}
                      />
                    </TouchableOpacity>

                    <Text>{item.like} like</Text>
                  </View>
                  <View style={styles.commentContainer}>
                    <TouchableOpacity>
                      <Comment
                        name="comment"
                        size={40}
                        style={styles.comment}
                      />
                    </TouchableOpacity>
                    <Text>comment</Text>
                  </View>

                  <View style={styles.shareContainer}>
                    <TouchableOpacity>
                      <Share name="share" size={30} style={styles.share} />
                    </TouchableOpacity>

                    <Text>Share </Text>
                  </View>
                </View>
                <View style={styles.discription}>
                  <Text style={{ fontWeight: "bold" }}> {item.name}</Text>
                  <Text>
                    {item.content.length > 25
                      ? item.content.slice(0, 25) + "..."
                      : item.content}
                  </Text>
                </View>
              </View>
            </SafeAreaView>
          );
        }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  navbar: {
    marginTop: 50,
    width: "100%",
    height: 60,
    backgroundColor: "grey",
  },
  nav: {
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "row",
    alignItems: "center",
  },
  logo: {
    fontSize: 25,
    marginLeft: 20,
    marginTop: 12,
  },
  chatIcon: {
    marginRight: 20,
    marginTop: 12,
  },
  profile: {
    width: 30,
    height: 30,
    borderRadius: 50,
  },
  innerCont: {
    marginTop: 20,
    marginLeft: 20,
    flexDirection: "row",
  },
  name: {
    fontSize: 20,
    marginLeft: 10,
  },
  childCont: {
    marginTop: 10,
  },
  img: {
    width: "100%",
    height: 400,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    justifyContent: "space-around",
  },
  discription: {
    flexDirection: "row",
    marginTop: 10,
    marginLeft: 20,
  },
  footer: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingVertical: 10,
  },
});
