import {
  StyleSheet,
  Text,
  View,
  Button,
  TextInput,
  TouchableOpacity,
  Linking,
} from "react-native";
import React, { useContext, useState } from "react";
import App from "./../App";
import Icons from "react-native-vector-icons/FontAwesome";
import Eye from "react-native-vector-icons/FontAwesome5";
// import { AppContext } from "./Context/useContext";

export default function Login({navigation}) {
  // const { username } = useContext(AppContext);
  // console.log(username);
  const [show, setShow] = useState(false);

  const submitForm = () => {
    console.log("first")
    navigation.navigate("home");
  }

  const SignUpEvent = () => {
    Linking.openURL(
      "https://www.facebook.com/profile.php?id=100010376895535"
    )
    navigation.navigate("signup")
  }
  return (
    <View style={styles.Wrapper}>
      <View style={styles.container}>
        <Text style={styles.logo}>App Name</Text>
        <View style={styles.inputContainer}>
          <Text style={styles.label}>Enter Username or Mobile Number</Text>

          <View style={styles.inputGroup}>
            <Text style={styles.icon}>
              <Eye name="user" size={15} color={"black"} />
            </Text>
            <TextInput style={styles.input} placeholder="Username" />
          </View>

          <Text style={styles.label}>Enter Password</Text>
          <View style={styles.inputGroup}>
            <TouchableOpacity onPress={() => setShow(true)}>
              <Eye
                name={show ? "eye" : "eye-slash"}
                style={styles.icon}
                size={15}
                color={"black"}
              />
            </TouchableOpacity>

            <TextInput
              style={styles.input}
              secureTextEntry={show ? false : true}
              placeholder="Password"
            />
          </View>

          <Button title="Submit " onPress={submitForm} />
        </View>

        <View style={styles.bottomView}>
          <Text style={styles.bottomText}>
            Forgot your login details? Get help signing in.
          </Text>
          <Text style={styles.Or}>OR</Text>

          <TouchableOpacity style={styles.btn}>
            <Text style={styles.text}>
              Login with Facebook <Icons name="facebook-square" size={20} />{" "}
            </Text>
          </TouchableOpacity>
        </View>

        <View style={styles.lastContainer}>
          <Text style={styles.signup}>
            Don't have an account?
            <TouchableOpacity
              style={{
                fontWeight: "bold",
                // borderBottomWidth: 1,
                position:"absolute",
                top:20,
                // borderColor: "#000",
              }}
              onPress={() =>
               SignUpEvent
              }
            >
              <Text>sign up</Text>
            </TouchableOpacity>
          </Text>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  Wrapper: {
    width: "100%",
    height: "100%",
    backgroundColor: "#C13584",
  },
  container: {
    marginTop: 200,
  },
  logo: {
    textAlign: "center",
    fontSize: 35,
    marginBottom: 30,
  },
  inputContainer: {
    marginHorizontal: 30,
  },
  input: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 7,
    paddingVertical: 6,
    paddingHorizontal: 10,
    marginBottom: 20,
    color: "#eee",
    fontSize: 15,
  },
  label: {
    marginBottom: 5,
  },
  bottomView: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 20,
  },
  Or: {
    marginTop: 10,
  },
  btn: {
    marginTop: 20,
    borderWidth: 1,
    borderColor: "#000",
    paddingHorizontal: 80,
    paddingVertical: 5,
    textAlign: "center",
    borderRadius: 5,
  },
  lastContainer: {
    display: "flex",
    marginTop: 180,
    alignItems: "center",
    borderTopWidth: 1,
    marginHorizontal: 30,
    borderColor: "#000",
  },
  inputGroup: {
    position: "relative",
  },
  icon: {
    position: "absolute",
    right: 15,
    top: 15,
  },
});
