import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  SafeAreaView,
  Image,
} from "react-native";
import React, { useState } from "react";
import { useNavigation } from "@react-navigation/native";

const Navigation = () => {
  const navigation = useNavigation();
  const [icon, setIcon] = useState("home");
  return (
    <SafeAreaView style={styles.footer}>
      <TouchableOpacity
        onPress={() => {
          setIcon("home");
          navigation.navigate("home");
        }}
      >
        {icon === "home" ? (
          <Text>
            <ShadedHomeIcon name="home" size={25} />
          </Text>
        ) : (
          <Text>
            <HomeIcon name="home" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          setIcon("search");
          navigation.navigate("search");
        }}
      >
        {icon === "search" ? (
          <Text>
            <ShadedSearchIcon name="search" size={25} />
          </Text>
        ) : (
          <Text>
            <Search name="search1" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("notification");
          setIcon("notify");
        }}
      >
        {icon === "notify" ? (
          <Text>
            <ShadedNotifyIcon name="notifications" size={25} />
          </Text>
        ) : (
          <Text>
            <Notify name="notifications-outline" size={25} />
          </Text>
        )}
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => {
          navigation.navigate("news");
          setIcon("news");
        }}
      >
        {icon === "news" ? (
          <Text>
            <NewsPaper name="newspaper" size={25} />
          </Text>
        ) : (
          <Text>
            <News name="news" size={25} />
          </Text>
        )}
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => {
          setIcon("profile");
          navigation.navigate("profile");
        }}
      >
        <Image source={require("../assets/icon.png")} style={styles.profile} />
      </TouchableOpacity>
    </SafeAreaView>
  );
};

export default Navigation;

const styles = StyleSheet.create({
  footer: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingVertical: 10,
  },
});
