import { StyleSheet, Text, TouchableOpacity, View, Image } from "react-native";
import React from "react";

export default function Chats() {
  return (
    <View>
      <View style={styles.container}>
        <Text style={{ fontSize: 20 }}>Messages</Text>
        <View style={styles.chatSection}>
          <TouchableOpacity>
            <Image source={require("../assets/img2.jpeg")} style={styles.img} />
          </TouchableOpacity>
          <Text>Zaid Siddiqui</Text>
          </View>

          <View style={{ flexDirection: "row" }}>
            <Text>Last Chat Message</Text>
            <Text>Time</Text>
          </View>
       
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    marginLeft: 15,
  },
  chatSection: {
    marginTop: 20,
    flexDirection:'row',
  },
  img: {
    width: 50,
    height: 50,
    borderRadius: 50,
  },
});
