import React from "react";
import { createContext, useState } from "react";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";

export const AuthContext = React.createContext({});

export const AuthProvider = ({ children }) => {
  const [userId, setUserId] = useState("");
  const [userInfo, setUserInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const getData = async () => {
    let userId = await AsyncStorage.getItem("userid");
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    let updatedUserData = JSON.parse(await AsyncStorage.getItem("updatedUser"));
    console.log("userData : ", userData);
    setUserInfo(userData.userName);
    setUserId(userId);
  };
  getData();

  const likePost = async (postId) => {
    console.log("Liked post id ", postId);
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    axios
      .put(`http://192.168.0.106:5000/api/like/${postId}`, {
        userId: userData._id,
      })
      .then((res) => {
        console.log("Response getting from posts", res);
        console.log(res.data.result.likes);
        // console.log(res.data.result.likes);

        if (
          userData &&
          res.data.result.likes.includes(userData._id)
          // like[i] === userData._id
        ) {
          console.log("first");
          setLiked(true);
        } else {
          setLiked(false);
        }
      })
      .catch((err) => console.log(err));
  };

  return (
    <AuthContext.Provider
      value={{ userId: userId, userInfo: userInfo, likePost: likePost }}
    >
      {children}
    </AuthContext.Provider>
  );
};
