import {
  StyleSheet,
  Text,
  View,
  Image,
  ScrollView,
  SafeAreaView,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  Modal,
  Pressable,
  TextInput,
} from "react-native";
import React, { useState, useContext, useEffect } from "react";
import Chat from "react-native-vector-icons/Ionicons";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import HomeIcon from "react-native-vector-icons/AntDesign";
import Search from "react-native-vector-icons/AntDesign";
import Notify from "react-native-vector-icons/Ionicons";
import Share from "react-native-vector-icons/FontAwesome";
import ShadedHomeIcon from "react-native-vector-icons/Entypo";
import ShadedSearchIcon from "react-native-vector-icons/FontAwesome";
import Times from "react-native-vector-icons/FontAwesome";
import ShadedNotifyIcon from "react-native-vector-icons/Ionicons";
import Logout from "react-native-vector-icons/AntDesign";
import Dislike from "react-native-vector-icons/AntDesign";
import AddPostIcon from "react-native-vector-icons/Ionicons";
import { AuthContext } from "./Context/useContext";
import { useNavigation } from "@react-navigation/native";
import Login from "./Login";
import AsyncStorage from "@react-native-async-storage/async-storage";
import News from "react-native-vector-icons/Entypo";
import NewsPaper from "react-native-vector-icons/Ionicons";
import axios from "axios";

export const Apps = () => {
  const navigation = useNavigation();
  const [token, setToken] = useState(null);
  const [userId, setUserId] = useState("");

  const retrieveData = async () => {
    const savedData = await AsyncStorage.getItem("token");
    const userData = JSON.parse(await AsyncStorage.getItem("user"));
    console.log("ID: ", userData._id);
    setUserId(userData._id);
    console.log("Saved Data is", savedData);
    console.log("user Data is", userData);
    setToken(savedData);
  };

  retrieveData();

  return <>{token ? <Home /> : <Login />}</>;
};

export default function Home() {
  return (
    <>
      <Navbar />
      <Navigations />
      <Main />
    </>
  );
}

export const Navbar = () => {
  const navigation = useNavigation();
  const loggedOut = async () => {
    const clearLs = AsyncStorage.clear();
    if (clearLs) {
      navigation.navigate("login");
    } else {
      alert("Storage is not cleared");
      navigation.navigate("home");
    }
  };
  return (
    <View style={styles.navbar}>
      <View style={styles.nav}>
        <Text style={styles.logo}>chattify</Text>
        <Text style={styles.chatIcon}>
          <TouchableOpacity onPress={() => navigation.navigate("upload")}>
            <AddPostIcon
              name="add-circle"
              size={30}
              style={{ paddingHorizontal: 10 }}
            />
          </TouchableOpacity>

          <TouchableOpacity onPress={() => navigation.navigate("chat")}>
            <Chat name="md-chatbubble-ellipses-outline" size={30} />
          </TouchableOpacity>

          <TouchableOpacity>
            <Logout name="logout" size={30} onPress={() => loggedOut()} />
          </TouchableOpacity>
        </Text>
      </View>
    </View>
  );
};

export const Navigations = () => {
  const navigation = useNavigation();

  const [icon, setIcon] = useState("home");
  return (
    <SafeAreaView style={styles.footer}>
      <TouchableOpacity
        onPress={() => {
          setIcon("home");
          navigation.navigate("home");
        }}
      >
        {icon === "home" ? (
          <Text>
            <ShadedHomeIcon name="home" size={25} />
          </Text>
        ) : (
          <Text>
            <HomeIcon name="home" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          setIcon("search");
          navigation.navigate("search");
        }}
      >
        {icon === "search" ? (
          <Text>
            <ShadedSearchIcon name="search" size={25} />
          </Text>
        ) : (
          <Text>
            <Search name="search1" size={25} />
          </Text>
        )}
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => {
          navigation.navigate("notification");
          setIcon("notify");
        }}
      >
        {icon === "notify" ? (
          <Text>
            <ShadedNotifyIcon name="notifications" size={25} />
          </Text>
        ) : (
          <Text>
            <Notify name="notifications-outline" size={25} />
          </Text>
        )}
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => {
          navigation.navigate("news");
          setIcon("news");
        }}
      >
        {icon === "news" ? (
          <Text>
            <News name="news" size={25} />
          </Text>
        ) : (
          <Text>
            <NewsPaper name="newspaper" size={25} />
          </Text>
        )}
      </TouchableOpacity>

      <TouchableOpacity
        onPress={() => {
          setIcon("profile");
          navigation.navigate("profile");
        }}
      >
        <Image source={require("../assets/img1.jpeg")} style={styles.profile} />
      </TouchableOpacity>
    </SafeAreaView>
  );
};

export const Main = () => {
  const [postData, setPostData] = useState([]);
  const [Id, setId] = useState("");
  const [show, setShow] = useState(false);
  const [likesCount, setLikesCount] = useState(0);
  const [likeAction, setLikeAction] = useState(null);
  const [openModel, setOpenModel] = useState(false);
  const [comment, setComment] = useState("");
  const navigation = useNavigation();

  const getData = async () => {
    let userData = JSON.parse(await AsyncStorage.getItem("user"));

    axios.get("http://192.168.0.106:5000/api/allpost").then((res) => {
      setPostData(res.data.data);
      console.log("res : ", res.data.data[0].postedBy.profile);
    });
  };

  useEffect(() => {
    getData();
  }, []);

  const likePost = async (id) => {
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    axios
      .put("http://192.168.0.106:5000/api/like", {
        postId: id,
        userId: userData._id,
      })
      .then((res) => {
        console.log(res);
        setShow(!show);
        setLikesCount(res.data.result.likes.length);
        // setLikeAction("You likes this post");
        console.log("Likes COunt", res.data.result.likes.length);
      })
      .catch((err) => console.log(err));
  };

  const unLikePost = async (id) => {
    let userData = JSON.parse(await AsyncStorage.getItem("user"));
    setShow(false);
    // console.log(userData);
    axios
      .put("http://192.168.0.106:5000/api/unlike", {
        postId: id,
        userId: userData._id,
      })
      .then((res) => {
        console.log("Unlike Response : ", res);
        setLikesCount(res.data.result.likes.length);
      })
      .catch((err) => console.log(err));
  };
  //
  return (
    <>
      <FlatList
        data={postData}
        // keyExtractor={(item) => item.data[._id}
        renderItem={({ item, index }) => {
          console.log("Items are :", item);
          return (
            <SafeAreaView style={styles.container}>
              <View style={styles.innerCont}>
                <Image
                  source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
                  style={styles.profile}
                />
                <Text style={styles.name}>{item.postedBy.userName}</Text>
              </View>
              <View style={styles.childCont}>
                <Image source={{ uri: item.image }} style={styles.img} />
                <View style={styles.iconsContainer}>
                  <View style={styles.likeContainer}>
                    <TouchableOpacity>
                      {show ? (
                        <Dislike
                          name="dislike2"
                          size={30}
                          style={styles.like}
                          onPress={() => unLikePost(item._id)}
                        />
                      ) : (
                        <Like
                          name="like"
                          size={30}
                          backgroundColor="blue"
                          style={styles.like}
                          onPress={() => likePost(item._id)}
                        />
                      )}
                    </TouchableOpacity>

                    <Text>{likesCount == 0 ? "" : likesCount}like</Text>
                  </View>
                  <View style={styles.commentContainer}>
                    <TouchableOpacity>
                      <Comment
                        name="comment"
                        size={40}
                        style={styles.comment}
                        onPress={() => setOpenModel(true)}
                      />
                    </TouchableOpacity>
                    <Text>comment</Text>
                  </View>

                  <View style={styles.shareContainer}>
                    <TouchableOpacity>
                      <Share name="share" size={30} style={styles.share} />
                    </TouchableOpacity>

                    <Text>Share </Text>
                  </View>
                </View>
                <View style={styles.discription}>
                  <Text style={{ fontWeight: "bold" }}>
                    {item.content == "" ? "" : item.postedBy.userName}
                  </Text>
                  <Text>
                    {item.content.length > 25
                      ? item.content.slice(0, 25) + "..."
                      : item.content}
                  </Text>
                </View>
              </View>
            </SafeAreaView>
          );
        }}
      />

      <Modal visible={openModel} animationType="slide">
        <View>
          <Times
            name="times"
            size={30}
            style={{ margin: 20 }}
            onPress={() => setOpenModel(false)}
          />
          <TextInput
            style={styles.input}
            placeholder="Write Something here..."
            value={comment}
            onChangeText={(val) => setComment(val)}
          />
          <View style={styles.innerCont}>
            <Image
              source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
              style={{ width: 20, height: 20, borderRadius: 50 }}
            />
            <Text style={{ fontSize: 15 }}>Zaid Siddiqui</Text>
          </View>
        </View>
      </Modal>
    </>
  );
};

