import {
  StyleSheet,
  Text,
  View,
  TextInput,
  FlatList,
  TouchableOpacity,
  Image,
} from "react-native";
import React from "react";
import SearchIcon from "react-native-vector-icons/Feather";
import AddPostIcon from "react-native-vector-icons/Ionicons";

export default function Search() {
  return (
    <View style={styles.container}>
      <View style={styles.inputGrp}>
        <SearchIcon
          name="search"
          size={20}
          style={{ marginLeft: 10, textAlign: "center", marginTop: 3 }}
        />
        <TextInput placeholder="search" style={styles.search} />
      </View>

      <View>
        <Image
          source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
          style={styles.profile}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginTop: 55,
  },
  inputGrp: {
    flexDirection: "row",
    borderWidth: 1,
    borderColor: "#000",
    marginHorizontal: 20,
    borderRadius: 7,
    paddingVertical: 5,
  },
  search: {
    paddingHorizontal: 15,
    fontSize: 18,
  },
  // imageContainer: {
  //   marginTop: 10,
  //   flexDirection: "row",
  //   flex: 1,
  // },
  // image: {
  //   width: 120,
  //   height: 120,
  //   resizeMode: "cover",
  // },
  profile: {
    width: 50,
    height: 50,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },
});
