import { Image, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { useNavigation } from "@react-navigation/native";
import { io } from "socket.io-client";

const Conversation = ({ conversation, currentUser, msg }) => {
  const navigation = useNavigation();
  const [messages, setMessages] = useState([]);
  const [friendId, setFriendId] = useState([]);
  const [user, setUser] = useState([]);
  const [arrivalMessage, setArrivalMessage] = useState(null);
  // const socket = useRef();

  useEffect(() => {
    const socket = io("ws://localhost:3000");

    socket.on("getMessage", (data) => {
      setArrivalMessage({
        sender: data.senderId,
        text: data.text,
        createdAt: Date.now(),
      });
    });
  }, []);

  useEffect(() => {
    arrivalMessage &&
      conversation?.members.includes(arrivalMessage.sender) &&
      setMessages((prev) => [...prev, arrivalMessage]);
  }, [arrivalMessage, conversation]);

  useEffect(() => {
    io.emit("addUser", currentUser);
    io.on("getUsers", (users) => {
      setOnlineUsers(
        user.followings.filter((f) => users.some((u) => u.userId === f))
      );
    });
  }, [user]);

  const receiverId = conversation.members.find(
    (member) => member !== currentUser
  );

  io.emit("sendMessage", {
    senderId: currentUser,
    receiverId,
    text: msg,
  });

  useEffect(() => {
    const friendId = conversation.members.find((m) => m !== currentUser);
    setFriendId(friendId);
    console.log("Friend ", friendId);

    const getUser = async () => {
      try {
        const res = await axios(
          `http://192.168.0.106:5000/api/profile/${friendId}`
        );
        setUser(res.data.user);
      } catch (err) {
        console.log(err);
      }
    };
    getUser();
  }, [currentUser, conversation]);

  const getMessages = async (conversationId) => {
    console.log("conversationId", conversationId);
    try {
      const res = await axios.get(
        `http://192.168.0.106:5000/chat/getmessage/${conversationId}`
      );
      console.log(res);
      setMessages(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <View>
      <View style={styles.chatSection}>
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("message", {
              conversationId: conversation._id,
              friendId: friendId,
            });
            getMessages(conversation._id);
          }}
        >
          <View style={{ flexDirection: "row", position: "relative" }}>
            <Image source={{ uri: user.profile }} style={styles.img} />
            <Text style={{ marginLeft: 10, fontWeight: "500", fontSize: 20 }}>
              {user.name}
            </Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default Conversation;

const styles = StyleSheet.create({
  chatSection: {
    marginTop: 20,
    // flexDirection:'row',
  },
  img: {
    width: 50,
    height: 50,
    borderRadius: 50,
  },
});
