import { StyleSheet, Text, View, Image, FlatList } from "react-native";
import React, { useState, useEffect } from "react";
import Axios from "axios";

const News = () => {
  const [data, setData] = useState([]);
  const getData = () => {
    Axios.get(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=55d7bf49be2b471e823fc66cacc0fb93"
    )
      .then((res) => {
        console.log(res);
        setData(res.data.articles);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Text style={{ fontSize: 25 }}>News</Text>
      <FlatList
        data={data}
        keyExtractor={(item) => item.title}
        renderItem={({ item }) => {
          console.log(item);
          return (
            <View style={styles.container}>
              <View style={styles.card}>
                <Image source={{ uri: item.urlToImage }} style={styles.image} />
                <Text
                  style={{ marginTop: 5, fontWeight: "600", marginLeft: 10 }}
                >
                  {item.title}
                </Text>
                <Text
                  style={{ marginTop: 5, fontWeight: "400", marginLeft: 10 }}
                  numberOfLines={3}
                >
                  {item.content}
                </Text>
                <View
                  style={{
                    flexDirection: "row",
                    justifyContent: "space-between",
                    marginTop: 10,
                    marginHorizontal: 10,
                  }}
                >
                  <Text style={{ fontWeight: "400" }}>
                    By :{" "}
                    <Text style={{ fontWeight: "600" }}>{item.author}</Text>
                  </Text>
                  <Text style={{ fontWeight: "600" }}>Date </Text>
                </View>
                <Text
                  style={{ marginTop: 10, marginLeft: 10, fontWeight: "600" }}
                >
                  Source : {item.source.name}
                </Text>
              </View>
            </View>
          );
        }}
      />
    </>
  );
};

export default News;

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    alignSelf: "center",
    width: "90%",
  },
  card: {
    marginTop: 40,
    borderWidth: 1,
    borderColor: "#000",
    alignSelf: "center",
    width: 300,
    // borderRadius: 30,
    borderTopRightRadius: 30,
    borderTopLeftRadius: 30,
  },
  image: {
    borderTopRightRadius: 30,
    borderTopLeftRadius: 30,
    width: "100%",
    height: 200,
    // aspectRatio: 1,
  },
});
