import { StyleSheet, Text, View,Button ,TextInput, TouchableOpacity, Linking} from "react-native";
import React,{useState} from 'react'
import Icons from "react-native-vector-icons/FontAwesome"
import Eye from "react-native-vector-icons/FontAwesome5"
import Email from "react-native-vector-icons/MaterialCommunityIcons";
import { useNavigation } from "@react-navigation/native";
import DocumentPicker from "react-native-document-picker"

export default function SignUp() {
    const [show,setShow]=useState(false);
    
  const navigation = useNavigation();

  const openDoucmentFile = async() => {
    try{
      const res = await DocumentPicker.pick({
        type:[DocumentPicker.types.images],
      });
      console.log(res)
    }catch(err){
       console.log(err);
    }
  }
  return (
    <View>
       <View style={styles.Wrapper}>
    <View style={styles.container}>
      <Text style={styles.logo}>App Name</Text>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Enter Username </Text>

        <View style={styles.inputGroup}>
        <Text style={styles.icon} >
            <Eye name="user" size={15} color={'black'}/>
        </Text> 
        <TextInput style={styles.input} placeholder="Username" />
        </View>

        <Text style={styles.label}>Enter Name </Text>
        <View style={styles.inputGroup}>
        <Text style={styles.icon} >
            <Eye name="user" size={15} color={'black'}/>
        </Text> 
        <TextInput style={styles.input} placeholder="Full Name" />
        </View>
        
        <Text>Enter Email</Text>
        <View style={styles.inputGroup}>
        <Text style={styles.icon} >
            <Email name="email" size={15} color={'black'}/>
        </Text> 
        <TextInput style={styles.input} placeholder="xyz@gmail.com" keyboardType="email-address" />
        </View>
        

        <Text style={styles.label}>Enter Password</Text>
        <View style={styles.inputGroup}>
          <TouchableOpacity  onPress={() => setShow(true)}>
          <Eye name={show?"eye":"eye-slash"} style={styles.icon} size={15} color={'black'}/>
          </TouchableOpacity>
         

        <TextInput
          style={styles.input}
          secureTextEntry={show?false:true}
          placeholder="Password"
        />
        </View>
       
        <Text style={styles.label}>Confirm Password</Text>
        <View style={styles.inputGroup}>
          <TouchableOpacity  onPress={() => setShow(true)}>
          <Eye name={show?"eye":"eye-slash"} style={styles.icon} size={15} color={'black'}/>
          </TouchableOpacity>
        
        <TextInput
          style={styles.input}
          secureTextEntry={show?false:true}
          placeholder="Password"
        />
        </View>

       <TouchableOpacity onPress={openDoucmentFile}>
        <Text>Upload picture</Text>
       </TouchableOpacity>
       
        <Button title="Submit " />
      </View>


      <View style={styles.lastContainer}>
        <Text style={styles.signup}>
             Already have an account? 
             <Text style={{fontWeight:"bold",borderBottomWidth:1,borderColor:"#000",}} onPress={() =>(navigation.navigate("login"))}>Sign in</Text>
        </Text>
       
      </View>
    </View>
    </View>
    </View>
  )
}

const styles = StyleSheet.create({
    Wrapper:{
        width:'100%',
        height:'100%',
        backgroundColor:'#C13584',
    },
    container:{
        marginTop:200,
    },
    logo:{
      textAlign:"center",
      fontSize:35,
      marginBottom:30,
    },
    inputContainer:{
      marginHorizontal:30
    },
    input:{
        borderWidth:1,
        borderColor:'black',
        borderRadius:7,
        paddingVertical:6,
        paddingHorizontal:10,
        marginBottom:20,  
        color:"#eee",
        fontSize:15,
    },
    label:{
        marginBottom:5,
    },
    bottomView:{
      justifyContent:"center",
      alignItems:"center",
      marginTop:20,
    },
    Or:{
        marginTop:10,
    },
    btn:{
        marginTop:20,
        borderWidth: 1,
        borderColor: "#000",
        paddingHorizontal:80,
        paddingVertical:5,
        textAlign:"center",
        borderRadius:5,

    },
    lastContainer:{
        display:"flex",
      marginTop:20,
      alignItems:"center",
      borderTopWidth:1,
     marginHorizontal:30,
      borderColor: "#000",
    },
    inputGroup:{
       position:"relative",
    },
    icon:{
        position:"absolute",
        right:15,
        top:15,
    }
})
