import { StyleSheet, Text, TouchableOpacity, View, Image } from "react-native";
import React from "react";

export default function Chats() {
  return (
    <View>
      <View style={styles.container}>
        <Text style={{ fontSize: 20 }}>Messages</Text>
        <View style={styles.chatSection}>

            <View style={{flexDirection:"row",position:'relative'}}>
            <TouchableOpacity>
            <Image source={require("../assets/img2.jpeg")} style={styles.img} />
          </TouchableOpacity>
          <Text style={{marginLeft:10,fontWeight:"500",fontSize:20}}>Zaid Siddiqui</Text>
            </View>
          
          <View style={{position:"absolute"}}>
            <Text>Last Chat Message    2d</Text>
          </View>

        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    marginLeft: 15,
  },
  chatSection: {
    marginTop: 20,
    // flexDirection:'row',
  },
  img: {
    width: 50,
    height: 50,
    borderRadius: 50,
  },
});
