import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  FlatList,
} from "react-native";
import React, { useState } from "react";

const FindPeople = () => {
  const [data, setData] = useState([
    {
      id: 1,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
    {
      id: 2,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
    {
      id: 3,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
    {
      id: 4,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
    {
      id: 5,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
    {
      id: 6,
      image: require("../assets/img2.jpeg"),
      name: "Zaid SIdiqui",
      userName: "szaid_786",
    },
  ]);
  return (
    <View style={styles.boxContainer}>
      <FlatList
        numColumns={2}
        columnWrapperStyle={styles.row}
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => {
          return (
            <View style={styles.box}>
              <Image
                source={item.image}
                style={{ width: 110, height: 110, borderRadius: 100 }}
              />
              <View style={styles.boxBody}>
                <Text style={{ fontWeight: "600" }}>{item.userName}</Text>
                <Text>{item.name}</Text>
                <TouchableOpacity style={styles.btn}>
                  <Text>Follow</Text>
                </TouchableOpacity>
              </View>
            </View>
          );
        }}
      />
    </View>
  );
};

export default FindPeople;

const styles = StyleSheet.create({
  boxContainer: {
    width: "100%",
    // borderWidth: 1,
    // borderColor: "#000",
    justifyContent: "space-around",
    marginTop: 50,
    marginLeft: 20,
  },
  box: {
    borderWidth: 1,
    borderColor: "#000",
    width: 150,
    height: 200,
    justifyContent: "center",
    alignItems: "center",
  },
  boxBody: {
    justifyContent: "center",
    alignItems: "center",
  },
  btn: {
    marginTop: 5,
    backgroundColor: "#3498db",
    borderWidth: 1,
    borderColor: "#000",
    paddingVertical: 7,
    paddingHorizontal: 40,
    borderRadius: 5,
  },
  row: {
    flex: 1,
    justifyContent: "space-around",
  },
});
