import React, { useContext, useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
} from "react-native";
import Times from "react-native-vector-icons/FontAwesome";
import Check from "react-native-vector-icons/Feather";
import { useNavigation, Route, useRoute } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
// import { AlexBrush_400Regular } from "@expo-google-fonts/alex-brush";
// import { useFonts } from "expo-font";
// import AppLoading from "expo-app-loading";
import { AuthContext } from "./Context/useContext";

const EditProfile = ({ route }) => {
  const Navigation = useNavigation();
  const [userInfo, setUserInfo] = useState([]);
  const [name, setName] = useState(user.name);
  const [userName, setUserName] = useState(user.userName);
  const [bio, setBio] = useState("");

  const { userId } = route.params;

  const getData = async () => {
    const user = JSON.parse(await AsyncStorage.getItem("user"));
    console.log(user);
    // Fetching user data
    // axios
    //   .get(`http://192.168.0.106:5000/api/profile/${id}`)
    //   .then((res) => {
    //     console.log("Edit Data res : ", res);
    //     setUserInfo(res.data.user);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <View style={styles.wrapper}>
      <View
        style={{
          paddingTop: 40,
          backgroundColor: "#f1c40f",
          paddingVertical: 30,
          justifyContent: "space-around",
          flexDirection: "row",
          alignItems: "center",
        }}
      >
        <TouchableOpacity onPress={() => Navigation.goBack(null)}>
          <Times name="times" size={30} />
        </TouchableOpacity>
        <Text style={{ fontSize: 20, marginTop: 10 }}>Edit Profile</Text>
        <TouchableOpacity>
          <Check name="check" size={30} />
        </TouchableOpacity>
      </View>

      <View style={styles.topContainer}>
        <TouchableOpacity>
          <Image
            source={{
              uri:
                userInfo.profile == ""
                  ? "http://www.gravatar.com/avatar/?d=mp"
                  : userInfo.profile,
            }}
            style={styles.profile}
          />
        </TouchableOpacity>
        <TouchableOpacity>
          <Text style={{ marginTop: 10, fontSize: 20, color: "#2980b9" }}>
            Change Profile Photo
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.formContainer}>
        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Name</Text>
          <TextInput
            style={styles.input}
            value={userInfo.name}
            onChangeText={(val) => setNamee(val)}
          />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>UserName</Text>
          <TextInput
            style={styles.input}
            value={userInfo.userName}
            onChangeText={(val) => setUserName(val)}
          />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Add Bio</Text>
          <TextInput style={styles.input} onChangeText={(val) => setBio(val)} />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Add Link</Text>
          <TextInput style={styles.input} />
        </View>
      </View>
    </View>
  );
};

export default EditProfile;

const styles = StyleSheet.create({
  wrapper: {
    justifyContent: "space-between",
  },
  profile: {
    width: 100,
    height: 100,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },
  topContainer: {
    marginTop: 20,
    justifyContent: "center",
    alignItems: "center",
  },
  input: {
    borderBottomWidth: 1,
    borderBottomColor: "#000",
  },
  formContainer: {
    marginTop: 20,
  },
  inputGroup: {
    marginHorizontal: 20,
    marginVertical: 10,
  },
});
