import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React from "react";
import AddPostIcon from "react-native-vector-icons/Ionicons";
import { Image } from "react-native";

export default function Profile() {
  return (
    <View>
      <View style={styles.header}>
        <Text style={{ fontSize: 30, marginLeft: 15 }}>Username</Text>
        <TouchableOpacity>
          <Text style={{ marginRight: 15 }}>
            {" "}
            <AddPostIcon name="add-circle" size={30} />{" "}
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.mainContainer}>
        <View style={styles.main}>
          <View style={styles.Profile}>
            <Image source={require("../assets/img1.jpeg")} style={styles.img} />
            <Text style={{ textAlign: "center", fontWeight: "600" }}>
              Zaid Siddqui
            </Text>
          </View>

          <View style={styles.posts}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>Posts</Text>
          </View>

          <View style={styles.follwers}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followers</Text>
          </View>

          <View style={styles.following}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>following</Text>
          </View>
         
        </View>
        <TouchableOpacity style={styles.btn}>
          <Text>Edit Profile</Text>
        </TouchableOpacity>

       
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    marginTop: 40,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  main: {
    flexDirection: "row",
    justifyContent: "space-around",
    // alignItems:'center',
    marginTop: 30,
  },
  img: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
  bottomText: {
    fontWeight: "400",
  },
  btn:{
    borderWidth:1,
    borderColor:"#000",
    width:"65%",
    borderRadius:5,
  }
});
