import { StyleSheet, Text, View } from "react-native";
import React, { useState, useEffect } from "react";
import Axios from "axios";

const News = () => {
  const [data, setData] = useState([]);
  const getData = () => {
    Axios.get(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=55d7bf49be2b471e823fc66cacc0fb93"
    )
      .then((res) => {
        console.log(res);
        setData(res.data.articles);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 25 }}>News</Text>
      <View style={styles.card}>
        <Image
          source={require("https://images.unsplash.com/photo-1560707303-4e980ce876ad?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8c291cmNlfGVufDB8fDB8fA%3D%3D&auto=format&fit=crop&w=500&q=60")}
        />
      </View>
    </View>
  );
};

export default News;

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    alignSelf: "center",
  },
  card: {
    marginTop: 20,
    borderWidth: 1,
    borderColor: "#000",
    width: "90%",
    height: 200,
  },
});
