  //   let userData = JSON.parse(await AsyncStorage.getItem("user"));
  //   setUserInfo(userData.userName);
  //   setUserId(userId);
  // };

  // useEffect(() => {
  //   getData();
  // }, []);

  return (
    <AuthContext.Provider
      value={{
        userId,
        userInfo,
        dispatch,
        state,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
