import { StyleSheet, Text, View, Image, ScrollView, SafeAreaView } from "react-native";
import React from "react";
import Chat from "react-native-vector-icons/Ionicons";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";
import data from "./Data";

export default function Home() {
  return (
    <View>
      <View style={styles.navbar}>
        <View style={styles.nav}>
          <Text style={styles.logo}>chattify</Text>
          <Text style={styles.chatIcon}>
            <Chat name="md-chatbubble-ellipses-outline" size={30} />
          </Text>
        </View>
      </View>

        
        {
          data.map( (curElem,index) => {
            const {profile,name,image,content}=curElem;
           console.log(curElem);
            return (
              <SafeAreaView style={styles.container} key={index}>
              <ScrollView>
                <View style={styles.innerCont}>
                  <Image
                    source={require({profile})}
                    style={styles.profile}
                  />
                  <Text style={styles.name}>Zaid siddiqui</Text>
                </View>
                <View style={styles.childCont}>
                  <Image source={require("../assets/img1.jpeg")} style={styles.img} />
                  <View style={styles.iconsContainer}>
                    <View style={styles.likeContainer}>
                      <Like name="like" size={30} style={styles.like} />
                      <Text>1 like</Text>
                    </View>
                    <View style={styles.commentContainer}>
                      <Comment name="comment" size={40} style={styles.comment} />
                      <Text>comment</Text>
                    </View>
      
                    <View style={styles.shareContainer}>
                      <Share name="share" size={30} style={styles.share} />
                      <Text>Share </Text>
                    </View>
                  </View>
                  <View  style={styles.discription}>
                    <Text> zaid bolte bacchi</Text>
                  </View>
                </View>
              </ScrollView>
              </SafeAreaView>
            )
          })
        }

    </View>
  );
}

const styles = StyleSheet.create({
  navbar: {
    marginTop: 50,
    width: "100%",
    height: 60,
    backgroundColor: "grey",
  },
  nav: {
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "row",
    alignItems: "center",
  },
  logo: {
    fontSize: 25,
    marginLeft: 20,
    marginTop: 12,
  },
  chatIcon: {
    marginRight: 20,
    marginTop: 12,
  },
  profile: {
    width: 30,
    height: 30,
    borderRadius: 50,
  },
  innerCont: {
    marginTop: 20,
    marginLeft: 20,
    flexDirection: "row",
  },
  name: {
    fontSize: 20,
    marginLeft: 10,
  },
  childCont: {
    marginTop: 10,
  },
  img: {
    width: "100%",
    height: 400,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    justifyContent: "space-around",
  },
  discription:{
    marginTop:10,
    marginLeft:20,
  }
});
