import React from "react";
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  TextInput,
} from "react-native";
import Times from "react-native-vector-icons/FontAwesome";
import Check from "react-native-vector-icons/Feather";

const EditProfile = () => {
  return (
    <View style={styles.wrapper}>
      <View
        style={{
          paddingTop: 40,
          backgroundColor: "#f1c40f",
          paddingVertical: 30,
          justifyContent: "space-around",
          flexDirection: "row",
          alignItems: "center",
        }}
      >
        <TouchableOpacity>
          <Times name="times" size={30} />
        </TouchableOpacity>
        <Text style={{ fontSize: 20, marginTop: 10 }}>Edit Profile</Text>
        <TouchableOpacity>
          <Check name="check" size={30} />
        </TouchableOpacity>
      </View>

      <View style={styles.topContainer}>
        <TouchableOpacity>
          <Image
            source={{ uri: "http://www.gravatar.com/avatar/?d=mp" }}
            style={styles.profile}
          />
        </TouchableOpacity>
        <TouchableOpacity>
          <Text style={{ marginTop: 10, fontSize: 20, color: "#2980b9" }}>
            Change Profile Photo
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.formContainer}>
        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Name</Text>
          <TextInput style={styles.input} />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>UserName</Text>
          <TextInput style={styles.input} />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Add Bio</Text>
          <TextInput style={styles.input} />
        </View>

        <View style={styles.inputGroup}>
          <Text style={{ color: "#2980b9" }}>Add Link</Text>
          <TextInput style={styles.input} />
        </View>
      </View>
    </View>
  );
};

export default EditProfile;

const styles = StyleSheet.create({
  wrapper: {
    // backgroundColor: "#f1c40f",
    justifyContent: "space-between",
  },
  profile: {
    width: 100,
    height: 100,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },
  topContainer: {
    marginTop: 20,
    justifyContent: "center",
    alignItems: "center",
  },
  input: {
    borderBottomWidth: 1,
    borderBottomColor: "#000",
  },
  formContainer: {
    marginTop: 20,
  },
  inputGroup: {
    marginHorizontal: 20,
    marginVertical: 10,
  },
});
