import { Image, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React, { useEffect, useState } from "react";
import axios from "axios";

const Conversation = ({ conversation, currentUser }) => {
  const [user, setUser] = useState([]);
  useEffect(() => {
    const friendId = conversation.members.find((m) => m !== currentUser);
    console.log("Friend ", friendId);

    const getUser = async () => {
      try {
        const res = await axios(
          `http://192.168.0.106:5000/api/profile/${friendId}`
        );
        console.log(res.data.user);
        setUser(res.data.user);
      } catch (err) {
        console.log(err);
      }
    };
    getUser();
  }, [currentUser, conversation]);

  return (
    <View>
      <View style={styles.chatSection}>
        <View style={{ flexDirection: "row", position: "relative" }}>
          <TouchableOpacity>
            <Image source={require("../assets/img2.jpeg")} style={styles.img} />
          </TouchableOpacity>
          <Text style={{ marginLeft: 10, fontWeight: "500", fontSize: 20 }}>
            Zaid Siddiqui
          </Text>
        </View>

        <View style={{ position: "absolute", top: 25, left: 60 }}>
          <Text>Last Chat Message 2d</Text>
        </View>
      </View>
    </View>
  );
};

export default Conversation;

const styles = StyleSheet.create({
  chatSection: {
    marginTop: 20,
    // flexDirection:'row',
  },
  img: {
    width: 50,
    height: 50,
    borderRadius: 50,
  },
});
