import Send from "react-native-vector-icons/FontAwesome";
import React, { useState, useContext } from "react";
import { AuthContext } from "./Context/useContext";

const Message = () => {
  const [data, setData] = useState([
    {
      id: 1,
      text: "hello",
    },
