    // },
    // {
    //   id: 13,
    //   text: "What about You ? ",
    // },
    // {
    //   id: 14,
    //   text: "What about You ? ",
    // },
  ]);
  return (
    <>
      <View style={{ backgroundColor: "#f1c40f", paddingVertical: 20 }}>
        <Text
          style={{
            alignSelf: "center",
            marginTop: 30,
            fontSize: 20,
            backgroundColor: "#f1c40f",
          }}
        >
          Zaid Siddiqi
        </Text>
      </View>

      <FlatList
        data={data}
        keyExtractor={(key) => key.id}
        renderItem={({ item }) => {
          return (
            <SafeAreaView>
              <View
                style={{
                  justifyContent: "flex-end",
                  alignItems: "flex-end",
                  margin: 10,
                }}
              >
                <View style={styles.message}>
                  <Text>{item.text}</Text>
                </View>
                <Text style={{ marginRight: 20 }}> 8:30 Am</Text>
              </View>
            </SafeAreaView>
          );
        }}
      />

      <Textinput />
    </>
  );
};

export const Textinput = () => {
  const { userId } = useContext(AuthContext);
  console.log(userId);
  return (
    <View style={userId ? styles.User : styles.CurrentUser}>
      <TextInput placeholder="Enter a Mesage" style={styles.input} />
      <View style={{ position: "absolute", marginLeft: 50 }}>
        <TouchableOpacity>
          <Text>
            <Send name="send" size={25} />
          </Text>
        </TouchableOpacity>
      </View>

      {/* <div className={own ? "message own" : "message"}>
        <div className="messageTop">
          <p className="messageText">{message.text}</p>
        </div>
        <div className="messageBottom">{format(message.createdAt)}</div>
      </div> */}
    </View>
  );
};

const styles = StyleSheet.create({
  message: {
    borderRadius: 20,
    padding: 10,
    borderWidth: 1,
    borderColor: "#000",
    // margin: 10,
    width: 200,
  },
  input: {
    borderWidth: 1,
    borderColor: "#000",
    padding: 7,
    width: 350,
    borderRadius: 15,
  },
  CurrentUser: {
    justifyContent: "center",
    alignItems: "center",
    marginVertical: 20,
    position: "relative",
  },
  User: {
    justifyContent: "flex-start",
    alignItems: "center",
    marginVertical: 20,

    // position: "relative",
  },
});
export default Message;
