import {
  FlatList,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useContext, useEffect, useState } from "react";
import Like from "react-native-vector-icons/SimpleLineIcons";
import Comment from "react-native-vector-icons/EvilIcons";
import Share from "react-native-vector-icons/FontAwesome";
import AddPostIcon from "react-native-vector-icons/AntDesign";
import ViewPostIcon from "react-native-vector-icons/MaterialCommunityIcons";
import { AuthContext } from "./Context/useContext";
// import data from "./Data/Data";
import { Image } from "react-native";
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

export default function Profile() {
  const { userData } = useContext(AuthContext);
  const [userInfo, setUserInfo] = useState([]);
  const navigation = useNavigation();

  // useEffect(() => {
  //   getData();
  // }, []);

  const [data, setData] = useState("");

  useEffect(async () => {
    getData();
  }, []);

  const getData = async () => {
    const saved = JSON.parse(await AsyncStorage.getItem("user"));
    console.log("Saved Data is :", saved._id);
    console.log("Data is :", data);
    setData(saved._id);
    axios
      .get(`http://localhost:5000/api/mypost/${data}`)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // const getData = async () => {
  //   let data = JSON.parse(await AsyncStorage.getItem("user"));
  //   console.log(data);
  //   axios
  //     .get(`http://localhost:5000/api/profile/${data._id}`)
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  return (
    <View>
      <View style={styles.header}>
        <Text style={{ fontSize: 30, marginLeft: 15 }}>{data.userName}</Text>
        <TouchableOpacity>
          <Text style={{ marginRight: 15 }}>
            <AddPostIcon
              name="plussquareo"
              size={30}
              style={{ borderRadius: 50 }}
              onPress={() => navigation.navigate("upload")}
            />
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.mainContainer}>
        <View style={styles.main}>
          <View style={styles.Profile}>
            <Image
              source={{
                uri: data.Profile == "" ? "No Photo" : data.Profile,
              }}
              style={styles.img}
            />
            <Text style={{ textAlign: "center", fontWeight: "600" }}>
              {data.name}
            </Text>
          </View>

          <View style={styles.posts}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>Posts</Text>
          </View>

          <View style={styles.follwers}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followers</Text>
          </View>

          <View style={styles.following}>
            <Text style={{ textAlign: "center", fontWeight: "bold" }}>0 </Text>
            <Text style={styles.bottomText}>followings</Text>
          </View>
        </View>
        <TouchableOpacity style={styles.btn}>
          <Text style={{ textAlign: "center", fontWeight: "500" }}>
            Edit Profile
          </Text>
        </TouchableOpacity>
      </View>

      <View style={styles.postContainer}>
        <Text style={{ textAlign: "center", fontSize: 30, marginBottom: 10 }}>
          Your Posts
        </Text>
        {/* <FlatList
          data={data}
          keyExtractor={(item) => item.id}
          renderItem={({ item }) => {
            const { image, like } = item;
            return (
              <View>
                <Image
                  source={image}
                  style={{ height: 300, resizeMode: "cover" }}
                />

                <View style={styles.iconsContainer}>
                  <View style={styles.likeContainer}>
                    <Like
                      name="like"
                      size={30}
                      backgroundColor="blue"
                      style={styles.like}
                    />
                    <Text style={{ fontWeight: "600" }}> {like}like</Text>
                  </View>

                  <View style={styles.commentContainer}>
                    <Comment name="comment" size={40} style={styles.comment} />
                    <Text style={{ textAlign: "center", fontWeight: "600" }}>
                      {" "}
                      1546 comment
                    </Text>
                  </View>

                  <View style={styles.shareContainer}>
                    <Share name="share" size={30} style={styles.share} />
                    <Text style={{ fontWeight: "600" }}>3564 Share </Text>
                  </View>
                </View>
              </View>
            );
          }}
        /> */}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    marginTop: 40,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  main: {
    flexDirection: "row",
    justifyContent: "space-around",
    position: "relative",
    marginTop: 30,
  },
  img: {
    width: 100,
    height: 100,
    borderRadius: 50,
    borderWidth: 1,
    borderColor: "#000",
  },
  bottomText: {
    fontWeight: "400",
  },
  btn: {
    borderWidth: 1,
    borderColor: "#000",
    width: "60%",
    borderRadius: 5,
    paddingVertical: 3,
    textAlign: "center",
    position: "absolute",
    top: 80,
    left: 130,
    alignSelf: "center",
  },
  postContainer: {
    marginTop: 20,
  },
  iconsContainer: {
    marginTop: 15,
    flexDirection: "row",
    marginBottom: 20,
    justifyContent: "space-around",
    alignItems: "center",
  },
});
