import { Image, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigation } from "@react-navigation/native";

const Conversation = ({ conversation, currentUser, currentChat }) => {
  const navigation = useNavigation();
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState([]);
  useEffect(() => {
    const friendId = conversation.members.find((m) => m !== currentUser);
    console.log("Friend ", friendId);

    const getUser = async () => {
      try {
        const res = await axios(
          `http://192.168.0.106:5000/api/profile/${friendId}`
        );
        setUser(res.data.user);
      } catch (err) {
        console.log(err);
      }
    };
    getUser();
  }, [currentUser, conversation]);

  const getMessages = async () => {
    console.log(currentChat);
    try {
      const res = await axios.get(
        `http://192.168.0.106:5000/chat/getmessage/${currentChat?._id}`
      );
      console.log(res);
      setMessages(res.data);
    } catch (err) {
      console.log(err);
    }
  };
  //   useEffect( () => {
  //       getMessages();
  //   },[])

  return (
    <View>
      <View style={styles.chatSection}>
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("message");
            getMessages();
          }}
        >
          <View style={{ flexDirection: "row", position: "relative" }}>
            <Image source={{ uri: user.profile }} style={styles.img} />
            <Text style={{ marginLeft: 10, fontWeight: "500", fontSize: 20 }}>
              {user.name}
            </Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default Conversation;

const styles = StyleSheet.create({
  chatSection: {
    marginTop: 20,
    // flexDirection:'row',
  },
  img: {
    width: 50,
    height: 50,
    borderRadius: 50,
  },
});
